mapper.select:

select userId, productId, count
from cart
where userId = #{userId}


    	select count(*) 
    	from cart
		where productId = #{productId} and userId = #{userId}

mapper.update:
    	update cart set
    	count = #{count}
		where productId = #{productId} and userId = #{userId}

mapper.select:
select count
from cart
where productId = #{productId} and userId = #{userId}

mapper.delete:
delete 
from cart 
where userId = #{userId} and productId = #{productId}
