mapper.select:

		select rnum, qnaid, userid, qnatitle, qnacontent, qnaimage, qnadate, qnaanswer
        from (
           select rownum as rnum, qnaid, userid, qnatitle, qnacontent, qnaimage, qnadate, qnaanswer
           from (
              select qnaid, userid, qnatitle, qnacontent, qnaimage, qnadate, qnaanswer
              from qna
              order by qnaid desc
           )
           where rownum <=#{endRowNo}

        )
        where rnum >=#{startRowNo}
