mapper.select:

		select rnum, qnaid, username, qnatitle, qnacontent, qnaimage, qnadate, qnaanswer
        from (
           select rownum as rnum, qnaid, username, qnatitle, qnacontent, qnaimage, qnadate, qnaanswer
           from (
              select qnaid, u.username as username, qnatitle, qnacontent, qnaimage, qnadate, qnaanswer
              from qna q inner join userinfo u on q.userid = u.userid
              order by qnaid desc
           )
           where rownum <=#{endRowNo}

        )
        where rnum >=#{startRowNo}

mapper.insert:

	select qna_seq.nextval from dual

insert into qna (
		qnaid, userid, qnatitle, qnacontent, qnadate, qnaimage, qnaAttachType, qnaAttachFileName
) values(
		#{qnaId}, #{userId}, #{qnaTitle}, #{qnaContent}, sysdate,
		#{qnaImage, jdbcType=BLOB},
		#{qnaAttachType, jdbcType=VARCHAR},
		#{qnaAttachFileName, jdbcType=VARCHAR}
)

mapper.select:
select qnaid, u.username, qnatitle, qnacontent, qnadate, qnaimage, qnaAttachType, qnaAttachFileName
from qna q inner join userinfo u on q.userid = u.userid
where qnaid=#{qnaId}
