mapper.update:
	update userinfo set
 		userAttachType = #{userAttachType, jdbcType=VARCHAR},
	 	userImageData = #{userImageData, jdbcType=BLOB}
		where userId=#{userId}

	update userinfo set
 		userName = #{userName},	userPassword = #{userPassword},
 		userPhone=#{userPhone}, userAddress=#{userAddress}
		where userId=#{userId}

	update userinfo set
 		userName = #{userName},
 		userPhone=#{userPhone}, userAddress=#{userAddress}
		where userId=#{userId}
